const now = new Date();
const dow = (now.getDay() + 6) % 7;                // Monday=0‚Ä¶Sunday=6
const startOfWeek = new Date(now.getFullYear(), now.getMonth(), now.getDate() - dow, 0, 0, 0, 0);

const names = ['Sunday','Monday','Tuesday','Wednesday','Thursday','Friday','Saturday'];
const today = names[now.getDay()];
const sched = {
  Monday:    [],
  Tuesday:   [],
  Wednesday: [],
  Thursday:  [],
  Friday:    [],
  Saturday:  [],
  Sunday:    []
};

const items = $input.all() || [];
const todayStr = now.toDateString();

// 1) –ü–æ—á–∞—Ç–∫–æ–≤—ñ –∫—Ä–æ–∫–∏ –±–µ–∑ —Ç–∏—Ö, —â–æ completed=true —ñ –æ–Ω–æ–≤–ª–µ–Ω—ñ —Å—å–æ–≥–æ–¥–Ω—ñ
let steps = (sched[today] || []).filter(step => {
  const rec = items.find(i => i.json.step_name === step);
  return !(rec && rec.json.completed && new Date(rec.json.updated).toDateString() === todayStr);
});

// 2) –î–æ–¥–∞—Ç–∏ —Ç—ñ, —â–æ completed=false –π –æ–Ω–æ–≤–ª–µ–Ω—ñ —Ü—å–æ–≥–æ —Ç–∏–∂–Ω—è
items.forEach(({ json: { step_name, completed, updated } }) => {
  const upd = new Date(updated);
  if (!completed && upd >= startOfWeek && !steps.includes(step_name)) {
    steps.push(step_name);
  }
});

return [{ json: { output: true, steps: [...new Set(steps)] } }];

// collect our new items here
const returnData = [];

for (const item of items) {
  const raw = item.json.text || '';

  // 1. Extract whatever‚Äôs between the ```json fence or a plain ``` fence
  //    ‚Äì the (?:json)? makes the ‚Äújson‚Äù part optional
  const fenceRx = /```(?:json)?[\r\n]+([\s\S]*?)```/m;
  const match   = raw.match(fenceRx);

  // 2. If it matched, use the captured group; otherwise strip any leading/trailing ``` and trim
  const jsonText = match
    ? match[1]
    : raw.replace(/^```|```$/g, '').trim();

  // 3. Parse it (could be an object or an array)
  let parsed;
  try {
    parsed = JSON.parse(jsonText);
  } catch (err) {
    throw new Error('JSON parse failed: ' + err.message);
  }

  // 4. Normalize to an array
  const entries = Array.isArray(parsed) ? parsed : [parsed];

  // 5. Convert any remaining "\n" or "\\n" in output into real newlines, then emit
  for (const entry of entries) {
    if (typeof entry.output === 'string') {
      entry.output = entry.output
        .replace(/\\\\n/g, '\n')
        .replace(/\\n/g,   '\n');
    }
    returnData.push({ json: entry });
  }
}

return returnData;

