    const weekStart = new Date(today.setDate(today.getDate() - (today.getDay() === 0 ? 6 : today.getDay() - 1)));
    weekStart.setHours(0, 0, 0, 0);

    // Get Sunday 23:59 of this week
    const weekEnd = new Date(weekStart);
    weekEnd.setDate(weekStart.getDate() + 6);
    weekEnd.setHours(23, 59, 59, 999);

    // Adjust event start and end to fit within this week
    const effectiveStart = eventStart < weekStart ? weekStart : eventStart;
    const effectiveEnd = eventEnd > weekEnd ? weekEnd : eventEnd;

    // Calculate days within this week
    return Math.max(0, Math.ceil((effectiveEnd - effectiveStart) / (1000 * 60 * 60 * 24)));
  })()
}}

