const now = new Date();
const dow = (now.getDay() + 6) % 7;                // Monday=0‚Ä¶Sunday=6
const startOfWeek = new Date(now.getFullYear(), now.getMonth(), now.getDate() - dow, 0, 0, 0, 0);

const names = ['Sunday','Monday','Tuesday','Wednesday','Thursday','Friday','Saturday'];
const today = names[now.getDay()];
const sched = {
  Monday:    ['workload_today','workload_nextweek','connects_thisweek'],
  Tuesday:   ['workload_today'],
  Wednesday: ['workload_today'],
  Thursday:  ['workload_today'],
  Friday:    ['workload_today','day_off_nextweek'],
  Saturday:  [],
  Sunday:    []
};

const items = $input.all() || [];
const todayStr = now.toDateString();

// 1) –ü–æ—á–∞—Ç–∫–æ–≤—ñ –∫—Ä–æ–∫–∏ –±–µ–∑ —Ç–∏—Ö, —â–æ completed=true —ñ –æ–Ω–æ–≤–ª–µ–Ω—ñ —Å—å–æ–≥–æ–¥–Ω—ñ
let steps = (sched[today] || []).filter(step => {
  const rec = items.find(i => i.json.step_name === step);
  return !(rec && rec.json.completed && new Date(rec.json.updated).toDateString() === todayStr);
});

// 2) –î–æ–¥–∞—Ç–∏ —Ç—ñ, —â–æ completed=false –π –æ–Ω–æ–≤–ª–µ–Ω—ñ —Ü—å–æ–≥–æ —Ç–∏–∂–Ω—è
items.forEach(({ json: { step_name, completed, updated } }) => {
  const upd = new Date(updated);
  if (!completed && upd >= startOfWeek && !steps.includes(step_name)) {
    steps.push(step_name);
  }
});

return [{ json: { output: true, steps: [...new Set(steps)] } }];

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  item.json.myNewField = 1;
}

return $input.all();

return items.map(item => {
  const raw = item.json.output;
  const topSurvey = item.json.survey;

  // Helper to build the result, adding survey only if defined
  const makeResult = (out, survey) => {
    const res = { output: out };
    if (survey !== undefined) {
      res.survey = survey;
    }
    return res;
  };

  // 1) If it's already an object with its own `.output`, unwrap it:
  if (raw && typeof raw === 'object' && raw.output !== undefined) {
    let out = raw.output;
    // flatten one-deep arrays of {output:‚Ä¶}
    if (
      Array.isArray(out) &&
      out.length === 1 &&
      out[0] &&
      typeof out[0] === 'object' &&
      out[0].output !== undefined
    ) {
      out = out[0].output;
    }
    return makeResult(out, raw.survey);
  }

  // 2) Otherwise coerce to string and strip any ```json‚Ä¶``` fences:
  let inputStr = (typeof raw === 'string' ? raw : '').trim();
  const fenceRx = /^`{1,3}json\s*([\s\S]*?)\s*`{1,3}$/;
  const m = inputStr.match(fenceRx);
  if (m) inputStr = m[1].trim();

  // 3) Try JSON.parse, fallback to the raw string on failure:
  try {
    const parsed = JSON.parse(inputStr);

    // If the parsed value is an object with .output, unwrap that too:
    if (parsed && typeof parsed === 'object' && parsed.output !== undefined) {
      let out = parsed.output;
      if (
        Array.isArray(out) &&
        out.length === 1 &&
        out[0] &&
        typeof out[0] === 'object' &&
        out[0].output !== undefined
      ) {
        out = out[0].output;
      }
      return makeResult(out, parsed.survey);
    }

    // Otherwise it's some other JSON (array, primitive, etc.)
    return makeResult(parsed, parsed.survey);
  } catch {
    // Not valid JSON ‚Üí return the plain string, no survey
    return { output: inputStr };
  }
});

